$(document).ready(function () {
    $('.cSU').click(function (e) {
        e.preventDefault();
        const id = $(this).data('id');
        $.ajax({
            url: "/Admin/User/changeStatus",
            data: { id: id },
            dataType: 'json',
            type: 'POST',
            success: function (res) {
                if (res.Status == 1) {
                    $('.asdfasfdsfsdfasdfasdfasdfsdf' + id).css("text-decoration", "")
                    $('.asdfasfdsfsdfasdfasdfasdfsdf' + id).removeClass("text-danger");
                    $('.asdfasfdsfsdfasdfasdfasdfsdf' + id).addClass("text-success");
                    $('.asdfasfdsfsdfasdfasdfasdfsdf' + id).html("Hoạt Động");
                    toastr.success('Thay đổi trạng thái thành công! id=' + id);
                } else {
                    $('.asdfasfdsfsdfasdfasdfasdfsdf' + id).css("text-decoration", "underline")
                    $('.asdfasfdsfsdfasdfasdfasdfsdf' + id).removeClass("text-success");
                    $('.asdfasfdsfsdfasdfasdfasdfsdf' + id).addClass("text-danger");
                    $('.asdfasfdsfsdfasdfasdfasdfsdf' + id).html("Tạm khóa");
                    toastr.success('Thay đổi trạng thái thành công! id=' + id);
                }
            }
        });
    });
});

